Ext.define('Enif.view.charts.EloRatingViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.charts.elorating',

    getColor: function(index) {
        index = index ? index : 0;
        let colors = ["#115fa6", "#94ae0a", "#a61120", "#ff8809", "#ffd13e", "#a61187", "#24ad9a", "#7c7474", "#a66111"];

        if(index >= colors.length){
            index -= colors.length;
        }
        return colors[index];


    },

    onMylinechartInitialize: function(component, eOpts) {
        let playerData = Ext.getStore("PlayerData").getData();
        noPlayers = playerData.length;

        for(let i= 0; i< noPlayers; i++){
            let cl = this.getColor(i);
            component.addSeries({
                type: 'line',
                colors: [
                cl
                ],
                smooth: true,
                style: {
                    stroke: cl,
                    lineWidth: 3
                },
                marker: {
                    type: 'circle',
                    radius: 1
                },
                xField: 'timestamp',
                yField: 'player'+ i +'Rating',
                title: playerData.getAt(i).get('name'),
                tooltip: {
                    trackMouse: true,
                    width: 100,
                    height: 28,
                    renderer: function (toolTip, record, ctx) {
                        toolTip.setHtml(record.get('player' + ctx.field.replace(/[^0-9]/g,'') + 'Name') + ': ' + Math.round(record.get(ctx.field)));
                    }
                }
            });
        }


    }

});